import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [prompt, setPrompt] = useState('');

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!selectedImage) return;

    setLoading(true);
    try {
      const formData = new FormData();
      const response = await fetch(selectedImage);
      const blob = await response.blob();
      formData.append('image', blob, 'image.jpg');
      if (prompt) {
        formData.append('prompt', prompt);
      }

      const result = await fetch('http://localhost:5000/api/query', {
        method: 'POST',
        body: formData,
      });

      const data = await result.json();
      setResults(data.products || []);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ShopSmarter</h1>
        <p>Upload an image to find similar products</p>
      </header>

      <main>
        <form onSubmit={handleSubmit} className="upload-form">
          <div className="image-upload">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              id="image-upload"
            />
            <label htmlFor="image-upload">Choose an image</label>
            {selectedImage && (
              <img
                src={selectedImage}
                alt="Selected"
                className="preview-image"
              />
            )}
          </div>

          <div className="prompt-input">
            <input
              type="text"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="Add a description (optional)"
            />
          </div>

          <button type="submit" disabled={!selectedImage || loading}>
            {loading ? 'Searching...' : 'Find Similar Products'}
          </button>
        </form>

        <div className="results">
          {results.map((product, index) => (
            <div key={index} className="product-card">
              <img src={product.image_url} alt={product.title} />
              <h3>{product.title}</h3>
              {product.price && <p>Price: ${product.price}</p>}
              <a href={product.url} target="_blank" rel="noopener noreferrer">
                View Product
              </a>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
